import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';

const toDoList = [
  {
    id: 1,
    group: "Purchases",
    task: "Go to the bank",
    dependencyIds: [],
    completedAt: null
  },
  {
    id: 2,
    group: "Purchases",
    task: "Buy hammer",
    dependencyIds: [1],
    completedAt: null
  },
  {
    id: 3,
    group: "Purchases",
    task: "Buy wood",
    dependencyIds: [1],
    completedAt: null
  },
  {
    id: 4,
    group: "Purchases",
    task: "Buy nails",
    dependencyIds: [1],
    completedAt: null
  },
  {
    id: 5,
    group: "Purchases",
    task: "Buy paint",
    dependencyIds: [1],
    completedAt: null
  },
  {
    id: 6,
    group: "Build Airplane",
    task: "Hammer nails into wood",
    dependencyIds: [2, 3, 4],
    completedAt: null
  },
  {
    id: 7,
    group: "Build Airplane",
    task: "Paint wings",
    dependencyIds: [5, 6],
    completedAt: null
  },
  {
    id: 8,
    group: "Build Airplane",
    task: "Have a snack",
    dependencyIds: [],
    completedAt: null
  }
];


var completedPurchasesCounter = 0;
var completedAirplaneCounter = 0;

class SingleItem extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      'disabled': false,
      'completedAt' : '',
      'completed' : false,
      'itemId': this.props.id
    }

    if(this.props.id !== 1 && this.props.id !== 8){
      this.state = {
        'disabled': true ,
        'completedAt' : '',
        'completed' : false,
        'itemId': this.props.id

      }
    }

    this.toggleComplete = this.toggleComplete.bind(this);
    this.removeDisable = this.removeDisable.bind(this);
  }



  toggleComplete(e){
    //e.preventDefault();
    e.stopPropagation();

    //on checkbox isChecked, update completedAt with timestamp, else, remove timesamp(set to null)
    if(this.props.group === 'Purchases') {
      console.log(this.state);

      if (this.state.completed == false){

        completedPurchasesCounter++;


        this.setState({
          completedAt: new Date(),
          completed: true,
          completedPurchases: completedPurchasesCounter
        });

      } else {
        if(completedPurchasesCounter !== 0){
          completedPurchasesCounter--;
        }

        this.setState(prevState => ({
          completedAt: '',
          completed: false,
          completedPurchases: completedPurchasesCounter
        }));

      }

    } else {
      if (this.state.completedAt === ''){

        completedAirplaneCounter++;

        this.state = {
          completedAt: new Date(),
          completed: true
        }

        this.setState(prevState => ({
          completedAt: prevState,
          completed: prevState,
          completedPurchases: completedAirplaneCounter
        }));

      } else {
        if(completedAirplaneCounter !== 0){
          completedAirplaneCounter--;
        }

        this.setState(prevState => ({
          completedAt: '',
          completed: false,
          completedPurchases: completedAirplaneCounter
        }));

      }

    }

    this.removeDisable(); 

  }

  removeDisable(e){
    //console.log(this.props.dependencyIds);

    const ListItems = toDoList.map((todoItem) =>{

      var ids = todoItem.dependencyIds;
      var id = todoItem.id;

      //console.log(ids);
      //console.log(id);
      console.log(todoItem);

      ids.map((item) => {
        //console.log(item)
        let currentItemString = "itemID" + id;
        var currentItem = document.getElementById(currentItemString);

        let idString = "itemID" + item;
        var depenencyItem = document.getElementById(idString);

        //console.log(currentItem);
        //console.log(depenencyItem);



        if(depenencyItem !== null){
          if(!depenencyItem.checked === true){
             console.log("keep disabled :(");
             //console.log(this);
             //currentItem.addAttribute('disabled');
            //currentItem.setAttribute('disabled', true);
            console.log(currentItem.closest('.disabledLock'));

            let spanIdString = "spanID" + id;
            var lockSpan = document.getElementById(spanIdString);
            console.log(lockSpan);
            lockSpan.classList.add('disabledLock');
            lockSpan.classList.add('fa-lock');

            let labelIdString = "labelID" + id;
            var labelSpan = document.getElementById(labelIdString);
            console.log(labelSpan);
            labelSpan.classList.add('disabledLock');

            console.log("current state");
            console.log(this.state);

            this.setState(prevState => ({
              disabled: true,
            }));

          } else {
            console.log("remove disabled!");
            currentItem.removeAttribute('disabled');
            console.log(currentItem.closest('.disabledLock'));

            let spanIdString = "spanID" + id;
            var lockSpan = document.getElementById(spanIdString);
            console.log(lockSpan);
            lockSpan.classList.remove('disabledLock');
            lockSpan.classList.remove('fa-lock');

            let labelIdString = "labelID" + id;
            var labelSpan = document.getElementById(labelIdString);
            console.log(labelSpan);
            labelSpan.classList.remove('disabledLock');

            console.log("current state");
            console.log(this.state);

            this.setState(prevState => ({
              disabled: false,
            }));


          }
        } else {
          console.log('error, no dependency item');
        }

      });

    });





    //if the item with this dependency id# is NOT disabled (state is NOT disabled)
    //then this item is not disabled (state is not disabled)

  }

  render() {
    console.log(this.state);
    if(this.state.completedAt){
      if(this.state.completedAt !== ''){
        var checkComplete = true;
        var completedTaskClass = 'doneTextStyle';
        var disabledLock = '';

      } else {
        var checkComplete = false;
        var completedTaskClass = '';
        var disabledLock = 'fa fa-lock';
      }
    } else {
      var checkComplete = false;
      var completedTaskClass = '';
      if(this.state.completed === false && this.state.disabled === false){
        var disabledLock = '';
      } else {
        var disabledLock = 'fa fa-lock';
      }
    }

    if(this.state.itemId === 1){
      var disabledLock = '';
    }

    if(this.state.itemId === 8){
      var disabledLock = '';
    }

    //document.getElementById('spanID1').classList.remove('fa-lock');

    //this.removeDisable();

    return (
      <div key={this.props.id} className={"segment " + (this.props.group)}>
        <label className={"labelContainer disabledItem " + completedTaskClass } id={"labelID" + this.props.id}>{this.props.task}
          <input type="checkbox" checked={this.state.checkComplete} keyprop={this.props.id} id={"itemID" + this.props.id} value={this.props.id} onChange={this.toggleComplete} disabled={this.state.disabled}/>

          <span id={"spanID" + this.props.id} className={"checkmark checkmark-outline disabledCheckbox disabledLock " + disabledLock} aria-hidden="true"/>
        </label>
      </div>
    );
  }
}

