  showAirplaneList(e) {
    e.preventDefault();
    e.stopPropagation();

    this.setState(prevState => ({
      hideAirplaneList: !prevState,
      hidePurchaseDetails: prevState,
      hideAirplaneDetails: prevState,
      hideGroupsButton: !prevState
    }));

    this.updateHeading('airplane');
  }

  updateHeading(heading){
    var headingText = '';

    if(heading === 'purchase'){
      headingText = 'Purchases';

    } else if(heading === 'airplane'){
      headingText = 'Build Airplane';

    } else {

    }

    this.setState(prevState => ({
      headingText: headingText
    }));
  }

  update(data) {
    console.log("doing something");
    this.setState({
      completedPurchasesCounter: data
    });
  }

  showFullList(e) {
    e.preventDefault();
    e.stopPropagation();

    this.setState(prevState => ({
      hidePurchaseList: true,
      hideAirplaneList: true,
      hidePurchaseDetails: false,
      hideAirplaneDetails: false,
      hideGroupsButton: true,
      purchaseListCompletedCount: completedPurchasesCounter,
      airplaneListCompletedCount: completedAirplaneCounter,
      headingText: 'Things To Do'
    }));

  }

  render() {
    var totalPurchaseCounter = 0;
    var totalAirplaneCounter = 0;
    const ListItemCount = toDoList.map((item) =>{

      if(item.group === 'Purchases'){
        totalPurchaseCounter++;

      } else {
        totalAirplaneCounter++;
      }

    });

    //console.log(this);
    return (
      <div className="col-md-7 listwrap">
        <div className="container listHeadingWrap">
          <h4 className="listHeader">{this.state.headingText}</h4>
          <p onClick={this.showFullList} className="allGroupsBtn" hidden={this.state.hideGroupsButton}>All Groups</p>
        </div>
        <div>
          <div className="purchaseListWrap ">   
            <div className="container segment" onClick={this.showPurchaseList} hidden={this.state.hidePurchaseDetails}>     
              <label>Purchases</label>
              <p>{this.state.purchaseListCompletedCount} of {totalPurchaseCounter} tasks complete</p>
            </div>
            <div className="purchaseList" hidden= {this.state.hidePurchaseList}>
              <PurchaseTodoList data={this.update} completedCount={this.state.completedPurchasesCounter} />
            </div>
          </div>

          <div className="airplaneListWrap ">
            <div className="container segment" onClick={this.showAirplaneList} hidden={this.state.hideAirplaneDetails}>        
              <label>Build Airplane</label>
              <p>{this.state.airplaneListCompletedCount} of {totalAirplaneCounter} tasks complete</p>
            </div>

            <div className="airplaneList" hidden= {this.state.hideAirplaneList}>
              <AirplaneTodoList data={this.update} completedCount={this.state.purchaseListCompletedCount} />
            </div>
          </div>
        </div>
      </div>      
    );
  }
}


